import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent {
  title = 'app';
  selectedImage: File | null = null;
  imageBytes: ArrayBuffer | null = null;

  constructor(){}

  onFileSelected(event: any) {
    const files = event.target.files;
    this.selectedImage = files && files.length > 0 ? files[0] : null;
  }

  onUpload(): void {
    if (this.selectedImage) {
      const reader = new FileReader();
      reader.onloadend = () => {
        const imageBytes = reader.result as ArrayBuffer;
        this.formScannerApi.sendData(imageBytes).subscribe(
          response => {
            console.log('API Response:', response);
            // Handle the API response as needed
          },
          error => {
            console.error('API Error:', error);
            // Handle the API error as needed
          }
        );
      };

      reader.readAsArrayBuffer(this.selectedImage);
    }
  }
}
